import React, { Component } from "react";
import IconButton from "@material-ui/core/IconButton";
import ThumbsUpIcon from "@material-ui/icons/ThumbUp";
import ThumbsDownIcon from "@material-ui/icons/ThumbDown";

class Comment extends Component {
  constructor(props) {
    super(props);

    // const {text, upvotes, downvotes, username} = props;
    // console.log(text, upvotes, downvotes, username)

    this.state = {
      increment: 0,
      decrement: 0
    };
    this.increment = this.increment.bind(this);
    this.decrement = this.decrement.bind(this);
  }
  increment() {
    //post request add upvote
    this.setState({
      increment: this.state.increment + 1
    });
  }
  decrement() {
    //post request add downvote
    this.setState({
      decrement: this.state.decrement - 1
    });
  }
  render() {
    return (
      <div>
        <h3>kommentti</h3>
        <p>{this.props.text}</p>
        <p>käyttäjä: anonymous</p>
        <div>
          {this.state.increment}
          <IconButton
            aria-label="ThumbUp"
            className="countUp"
            onClick={this.increment}
          >
            <ThumbsUpIcon />
          </IconButton>
          <IconButton
            aria-label="Delete"
            className="countDown"
            onClick={this.decrement}
          >
            <ThumbsDownIcon />
          </IconButton>
          {this.state.decrement}
        </div>
      </div>
    );
  }
}

export default Comment;
